mapper.select:
SELECT p.*,e1.employeeName bpurchaseName,e2.employeeName checkName FROM branch_purchase_order p LEFT JOIN branch_employee e1 ON p.bpurchaseId=e1.employeeId LEFT JOIN branch_employee e2 ON p.checkId=e2.employeeId
and checkStatus like BINARY '%${selectName}%'
order by p.bpoId desc limit #{page},#{limit}

SELECT p.*,e1.employeeName bpurchaseName,e2.employeeName checkName FROM branch_purchase_order p LEFT JOIN branch_employee e1 ON p.bpurchaseId=e1.employeeId LEFT JOIN branch_employee e2 ON p.checkId=e2.employeeId where (checkStatus='未审核' or checkStatus='未批准')
and checkStatus like BINARY '%${selectName}%'
limit #{page},#{limit}
