mapper.select:
SELECT p.*,e1.employeeName returnName,e2.employeeName checkName FROM branch_purchase_return p LEFT JOIN branch_employee e1 ON p.returnId=e1.employeeId LEFT JOIN branch_employee e2 ON p.checkId=e2.employeeId
and checkStatus like BINARY '%${selectName}%'
order by p.bprId desc limit #{page},#{limit}

SELECT p.*,e1.employeeName returnName,e2.employeeName checkName FROM branch_purchase_return p LEFT JOIN branch_employee e1 ON p.returnId=e1.employeeId LEFT JOIN branch_employee e2 ON p.checkId=e2.employeeId where (checkStatus='未审核' or checkStatus='未批准')
and checkStatus like BINARY '%${selectName}%'
limit #{page},#{limit}
